-- Добавляем медицинские статьи
INSERT INTO medical_articles (title, content, excerpt, category_id, article_type, tags, author, medical_review_status, published) VALUES
-- Статьи о симптомах
('Головная боль: причины и лечение', 'Головная боль является одним из наиболее распространенных симптомов... Содержание статьи о головной боли, ее типах, причинах возникновения и методах лечения.', 'Узнайте о различных типах головной боли и эффективных методах лечения', (SELECT id FROM medical_categories WHERE name = 'Симптомы' LIMIT 1), 'symptom', ARRAY['головная боль', 'мигрень', 'боль'], 'Д-р Иванов А.И.', 'approved', true),

('Боль в груди: когда обращаться к врачу', 'Боль в груди может быть симптомом различных заболеваний... Подробное описание причин боли в груди и рекомендации по обращению за медицинской помощью.', 'Важная информация о боли в груди и сигналах для немедленного обращения к врачу', (SELECT id FROM medical_categories WHERE name = 'Симптомы' LIMIT 1), 'symptom', ARRAY['боль в груди', 'сердце', 'неотложная помощь'], 'Д-р Петров В.С.', 'approved', true),

('Повышенная температура: нормы и отклонения', 'Температура тела является важным показателем здоровья... Информация о нормальной температуре тела, причинах повышения и методах снижения.', 'Все о температуре тела: нормы, причины повышения и методы лечения', (SELECT id FROM medical_categories WHERE name = 'Симптомы' LIMIT 1), 'symptom', ARRAY['температура', 'лихорадка', 'жар'], 'Д-р Сидорова М.А.', 'approved', true),

('Одышка: виды и причины', 'Одышка или затрудненное дыхание может указывать на различные проблемы... Описание видов одышки, ее причин и необходимых действий при появлении симптома.', 'Разбираемся в причинах одышки и методах диагностики', (SELECT id FROM medical_categories WHERE name = 'Симптомы' LIMIT 1), 'symptom', ARRAY['одышка', 'дыхание', 'легкие'], 'Д-р Козлов Н.П.', 'approved', true),

('Боль в животе: локализация и причины', 'Боль в животе может иметь множество причин... Подробный анализ различных типов боли в животе и их возможных причин.', 'Понимание локализации боли в животе поможет определить причину', (SELECT id FROM medical_categories WHERE name = 'Симптомы' LIMIT 1), 'symptom', ARRAY['боль в животе', 'желудок', 'кишечник'], 'Д-р Морозова Е.В.', 'approved', true),

-- Статьи о заболеваниях
('Артериальная гипертензия: диагностика и лечение', 'Артериальная гипертензия - одно из самых распространенных заболеваний... Комплексная информация о диагностике, лечении и профилактике гипертонии.', 'Современные подходы к диагностике и лечению артериальной гипертензии', (SELECT id FROM medical_categories WHERE name = 'Заболевания' LIMIT 1), 'disease', ARRAY['гипертония', 'давление', 'сердечно-сосудистые заболевания'], 'Д-р Волков С.М.', 'approved', true),

('Сахарный диабет 2 типа: управление заболеванием', 'Сахарный диабет 2 типа требует постоянного контроля... Подробное руководство по управлению диабетом, включая диету, физическую активность и медикаментозное лечение.', 'Эффективные стратегии управления сахарным диабетом 2 типа', (SELECT id FROM medical_categories WHERE name = 'Заболевания' LIMIT 1), 'disease', ARRAY['диабет', 'сахар', 'эндокринология'], 'Д-р Лебедева Т.К.', 'approved', true),

('Остеохондроз позвоночника: лечение и профилактика', 'Остеохондроз является дегенеративным заболеванием позвоночника... Информация о современных методах лечения и профилактики остеохондроза.', 'Комплексный подход к лечению и профилактике остеохондроза', (SELECT id FROM medical_categories WHERE name = 'Заболевания' LIMIT 1), 'disease', ARRAY['остеохондроз', 'позвоночник', 'спина'], 'Д-р Новиков А.Р.', 'approved', true),

('Бронхиальная астма: контроль и лечение', 'Бронхиальная астма - хроническое воспалительное заболевание дыхательных путей... Современные методы контроля и лечения бронхиальной астмы.', 'Как эффективно контролировать бронхиальную астму', (SELECT id FROM medical_categories WHERE name = 'Заболевания' LIMIT 1), 'disease', ARRAY['астма', 'дыхание', 'аллергия'], 'Д-р Соколова И.Л.', 'approved', true),

('Гастрит: причины, симптомы и лечение', 'Гастрит - воспаление слизистой оболочки желудка... Подробная информация о причинах, симптомах и современных методах лечения гастрита.', 'Все о гастрите: от причин до эффективного лечения', (SELECT id FROM medical_categories WHERE name = 'Заболевания' LIMIT 1), 'disease', ARRAY['гастрит', 'желудок', 'пищеварение'], 'Д-р Федоров Д.А.', 'approved', true),

-- Статьи о диагностике
('Общий анализ крови: расшифровка показателей', 'Общий анализ крови - один из основных методов диагностики... Подробная расшифровка всех показателей общего анализа крови и их клиническое значение.', 'Научитесь понимать результаты общего анализа крови', (SELECT id FROM medical_categories WHERE name = 'Диагностика' LIMIT 1), 'guide', ARRAY['анализ крови', 'диагностика', 'лабораторные исследования'], 'Д-р Кузнецова О.П.', 'approved', true),

('ЭКГ: основы интерпретации', 'Электрокардиография - важный метод диагностики сердечных заболеваний... Основы чтения и интерпретации электрокардиограммы.', 'Понимание основ ЭКГ для выявления сердечных проблем', (SELECT id FROM medical_categories WHERE name = 'Диагностика' LIMIT 1), 'guide', ARRAY['ЭКГ', 'сердце', 'кардиология'], 'Д-р Орлов В.И.', 'approved', true),

('УЗИ органов брюшной полости: подготовка и интерпретация', 'Ультразвуковое исследование - безопасный метод диагностики... Информация о подготовке к УЗИ и понимании результатов исследования.', 'Все о подготовке к УЗИ и понимании результатов', (SELECT id FROM medical_categories WHERE name = 'Диагностика' LIMIT 1), 'guide', ARRAY['УЗИ', 'диагностика', 'брюшная полость'], 'Д-р Зайцева Н.В.', 'approved', true),

('МРТ головного мозга: показания и противопоказания', 'Магнитно-резонансная томография - современный метод диагностики... Подробная информация о показаниях, противопоказаниях и подготовке к МРТ головного мозга.', 'Когда необходимо МРТ головного мозга и как к нему подготовиться', (SELECT id FROM medical_categories WHERE name = 'Диагностика' LIMIT 1), 'guide', ARRAY['МРТ', 'головной мозг', 'неврология'], 'Д-р Романов П.С.', 'approved', true),

('Биохимический анализ крови: ключевые показатели', 'Биохимический анализ крови предоставляет важную информацию о состоянии организма... Расшифровка основных биохимических показателей крови.', 'Понимание ключевых показателей биохимического анализа крови', (SELECT id FROM medical_categories WHERE name = 'Диагностика' LIMIT 1), 'guide', ARRAY['биохимия', 'анализ крови', 'метаболизм'], 'Д-р Смирнова Ю.А.', 'approved', true),

-- Статьи о лечении
('Антибиотикотерапия: правила приема', 'Правильное применение антибиотиков критически важно... Информация о правильном приеме антибиотиков, курсе лечения и возможных побочных эффектах.', 'Как правильно принимать антибиотики для эффективного лечения', (SELECT id FROM medical_categories WHERE name = 'Лечение' LIMIT 1), 'guide', ARRAY['антибиотики', 'лечение', 'инфекции'], 'Д-р Павлов А.Н.', 'approved', true),

('Физиотерапия: виды и показания', 'Физиотерапия играет важную роль в лечении многих заболеваний... Обзор различных видов физиотерапевтических процедур и их применения.', 'Эффективность физиотерапии в лечении различных заболеваний', (SELECT id FROM medical_categories WHERE name = 'Лечение' LIMIT 1), 'guide', ARRAY['физиотерапия', 'реабилитация', 'восстановление'], 'Д-р Григорьева Л.М.', 'approved', true),

('Диетотерапия: основные принципы', 'Правильное питание - основа лечения многих заболеваний... Принципы диетотерапии и ее роль в лечении различных патологий.', 'Роль правильного питания в лечении заболеваний', (SELECT id FROM medical_categories WHERE name = 'Лечение' LIMIT 1), 'guide', ARRAY['диета', 'питание', 'лечебное питание'], 'Д-р Белова К.С.', 'approved', true),

-- Статьи о профилактике
('Профилактика сердечно-сосудистых заболеваний', 'Предупреждение сердечно-сосудистых заболеваний - приоритет современной медицины... Комплексные меры профилактики болезней сердца и сосудов.', 'Эффективные меры профилактики сердечно-сосудистых заболеваний', (SELECT id FROM medical_categories WHERE name = 'Профилактика' LIMIT 1), 'guide', ARRAY['профилактика', 'сердце', 'сосуды'], 'Д-р Жуков Р.В.', 'approved', true),

('Здоровый образ жизни: основные принципы', 'Здоровый образ жизни - основа долголетия и хорошего самочувствия... Практические рекомендации по ведению здорового образа жизни.', 'Практическое руководство по здоровому образу жизни', (SELECT id FROM medical_categories WHERE name = 'Профилактика' LIMIT 1), 'guide', ARRAY['здоровый образ жизни', 'профилактика', 'долголетие'], 'Д-р Макарова В.Г.', 'approved', true);

